<html><head><title>DDIT Result Fetcher(Web)</title> <link href='https://fonts.googleapis.com/css?family=Roboto:100' rel='stylesheet' type='text/css'> <link rel="stylesheet" href="https://code.getmdl.io/1.1.3/material.teal-cyan.min.css" /> <script src="https://storage.googleapis.com/code.getmdl.io/1.0.6/material.min.js"></script><link rel="stylesheet" href="https://fonts.googleapis.com/icon?family=Material+Icons"></head><body><div class="mdl-layout mdl-js-layout mdl-layout--fixed-header">      <header class="mdl-layout__header">         <div class="mdl-layout__header-row">            <span class="mdl-layout-title">DDIT Result Fetcher(Web)</span>		 </div>	  </header>	  <div class="mdl-layout__drawer">         <span class="mdl-layout-title">Menu</span>         <nav class="mdl-navigation">            <a class="mdl-navigation__link" href="../index.html">Home</a>            <a class="mdl-navigation__link" href="../predictor/index.php">DDIT Predictor</a>			<a class="mdl-navigation__link" href="index.php">DDIT Result Fetcher</a>         </nav>      </div>	  <main class="mdl-layout__content">         <div class="page-content">			<table>				<tr><td><h2>DDIT Result Fetcher(Web Version)</h2></td></tr>				<form action = "get-result.php" onsubmit = "return validate();"method = "post">					<tr>					<td>E-Governance ID : </td>					<td>						<div class="mdl-textfield mdl-js-textfield mdl-textfield--floating-label">							<input class="mdl-textfield__input" type="text" id="egovid" name="egovid" length = "10" required>							<label class="mdl-textfield__label" for="egovid">E-Governance ID</label>						</div>					</td>					</tr>					<tr>					<td>Date Of Birth(dd/mm/yyyy) : </td>					<td>						<div class="mdl-textfield mdl-js-textfield mdl-textfield--floating-label">							<input type="number" min="1"max="31" step="1" id="dob" name="dob" style="width:50px;" required>							&nbsp;&nbsp;&nbsp;							<input type="number" min="1"max="12" step="1" id="mob" name="mob" style="width:50px;" required>							&nbsp;&nbsp;&nbsp;							<input type="number" min="1990"max=<?php echo date('Y');?> step="1" id="yob" name="yob" style="width:100px;" required>													</div>					</td>					</tr>					<tr>					<td>Result Type : </td>					<td>						<label class="mdl-radio mdl-js-radio mdl-js-ripple-effect" for="type">						<input type="radio" id="type" value="int" name="type" class="mdl-radio__button">						<span class="mdl-radio__label">Internal</span>						</label>						&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;						<label class="mdl-radio mdl-js-radio mdl-js-ripple-effect" for="type2">						<input type="radio" id="type2" value="ext" name="type" class="mdl-radio__button" >						<span class="mdl-radio__label">External</span>						</label>					</td>					</tr>					<tr>					<td>Semester : </td>					<td>						<div class="mdl-textfield mdl-js-textfield mdl-textfield--floating-label">							<input class="mdl-textfield__input" type="number" min="1"max="8" step="1" id="sem" name="sem" required>							<label class="mdl-textfield__label" for="sem">Semester</label>						</div>					</td>					</tr>					<tr>					<td>Batch Year : </td>					<td>						<div class="mdl-textfield mdl-js-textfield mdl-textfield--floating-label">							<input class="mdl-textfield__input" type="number" min="2010"max="2015" step="1" id="curryear" name="curryear" required>							<label class="mdl-textfield__label" for="curryear">Batch Year</label>						</div>					</td>					</tr>					<tr>					<td><center><button class="mdl-button mdl-js-button mdl-button--raised mdl-js-ripple-effect mdl-button--accent" type = "submit">Get Result</button></center></td>					</tr>				</form>			</table>			</div>      </main></div><script type = "text/javascript">function validate(){  var id =  document.getElementById("egovid").value;   if(((/[0-9]{2}[a-zA-Z]{5}[0-9]{3}/.test(id)) || /[a-zA-Z]{1}[0-9]{2}[a-zA-Z]{2}[0-9]{5}/.test(id)) && id.length==10) {    return true;  }  else  {    return false;  }}</script></body></html>